script:
if (doc.get("opportunity")):
    opportunity_from = frappe.db.get_value("DocType", {"name": doc.get("opportunity")}, "name")
    doc.custom_opportunity_from = opportunity_from

if doc.get("items"):
    from_quotation = doc.items[0].get("prevdoc_docname")
    if from_quotation:
        source = frappe.db.get_value("Quotation", from_quotation, ["custom_opportunity_from"], as_dict=True)
        if source:
            doc.custom_opportunity_from = source.custom_opportunity_from

if doc.get("items"):
    from_order = doc.items[0].get("prevdoc_docname")
    if from_order:
        source = frappe.db.get_value("Sales Order", from_order, ["custom_opportunity_from"], as_dict=True)
        if source:
            doc.custom_opportunity_from = source.custom_opportunity_from

if not doc.custom_contract:
    frappe.throw(_("You must link a Contract before submitting this Sales Order."))

contract = frappe.get_doc("Contract", doc.custom_contract)
if contract.status != "Active":
    frappe.throw(_("The linked Contract must be signed before submitting the Sales Order."))

# Server Script: after_submit on Intercompany Stock Transfer
# Inter-company only; numbered AR/AP; batch-aware; GL->SLE fallback; safe rollback; deferred linking; user message on zero valuation
# Import-free, avoids getattr and augmented assignments

# -----------------------------
# CONFIG
# -----------------------------
SENDER_AR_BASE = "1010 - Intercompany Receivable"    # change if your numbering differs
RECEIVER_AP_BASE = "2010 - Intercompany Payable"     # change if your numbering differs

# If both sides resolve to zero valuation, allow the stock transfer to complete WITHOUT creating JEs and SHOW a message.
ALLOW_ZERO_VALUATION = 1   # 1 = allow & skip JE creation; 0 = block (cancel SEs and throw)

# If one side has a positive amount and the other resolves to zero, copy the non-zero amount to the other side for symmetric JEs.
SYNC_AMOUNT_FROM_COUNTERPART = 1  # 1 = enable; 0 = disable

# -----------------------------
# Helpers
# -----------------------------
def exists_se(voucher_no):
    if not voucher_no:
        return False
    if not frappe.db.exists("Stock Entry", voucher_no):
        return False
    try:
        se = frappe.get_doc("Stock Entry", voucher_no)
        return se.docstatus == 1
    except Exception:
        return False

def item_has_batch_no(item_code):
    return 1 if (frappe.db.get_value("Item", item_code, "has_batch_no") or 0) else 0

def is_batch_for_item(batch_id, item_code):
    return True if frappe.db.get_value("Batch", {"name": batch_id, "item": item_code}, "name") else False

def is_batch_expired_on(batch_id, ymd_date):
    exp = frappe.db.get_value("Batch", batch_id, "expiry_date")
    if not exp:
        return False
    try:
        return True if exp <= ymd_date else False
    except Exception:
        return False

def sum_abs_sle_value(voucher_no, company_name):
    # Authoritative valuation fallback from Stock Ledger
    rows = frappe.get_all(
        "Stock Ledger Entry",
        filters={
            "voucher_type": "Stock Entry",
            "voucher_no": voucher_no,
            "company": company_name,
            "is_cancelled": 0
        },
        fields=["stock_value_difference"]
    )
    total = 0.0
    for r in rows:
        try:
            svd = float(r.get("stock_value_difference") or 0)
        except Exception:
            svd = 0.0
        total = total + abs(svd)
    return total

def fetch_gl(voucher_no, company_name):
    if not voucher_no or not company_name:
        return []
    return frappe.get_all(
        "GL Entry",
        filters={"voucher_type": "Stock Entry", "voucher_no": voucher_no, "company": company_name},
        fields=["account", "debit", "credit"]
    )

# -----------------------------
# 0) Basic validations + inter-company enforcement
# -----------------------------
items = doc.get("items")
if not items:
    frappe.throw("No items found on this Intercompany Stock Transfer.")

for d in items:
    item_code = d.get("item_code")
    if not item_code:
        frappe.throw("Item code is required on each row.")
    try:
        qty = float(d.get("qty") or 0)
    except Exception:
        qty = 0.0
    if qty <= 0:
        frappe.throw(f"Quantity must be greater than zero for item {item_code}.")

issue_company = doc.get("issue_company")
receipt_company = doc.get("receipt_company")
if not issue_company or not receipt_company:
    frappe.throw("Both Issue Company and Receipt Company must be set.")
if issue_company == receipt_company:
    frappe.throw("Inter-company only: Issue Company and Receipt Company must be different.")

posting_date = doc.get("posting_date") or frappe.utils.nowdate()
posting_time = doc.get("posting_time") or frappe.utils.nowtime()
set_posting_time = 1 if doc.get("set_posting_time") else 0

# Early batch validation (avoid opaque submit-time errors)
for d in items:
    item_code = d.get("item_code")
    if item_has_batch_no(item_code):
        b = d.get("batch_no")
        if not b:
            frappe.throw(f"Item {item_code} requires a Batch No (batch_no).")
        if not is_batch_for_item(b, item_code):
            frappe.throw(f"Batch {b} does not belong to Item {item_code}.")
        if is_batch_expired_on(b, posting_date):
            frappe.throw(f"Batch {b} is expired on posting date {posting_date}.")

# -----------------------------
# 0.a) PRE-CHECK: resolve AR/AP accounts (numbered) or throw
# -----------------------------
issue_abbr = frappe.db.get_value("Company", issue_company, "abbr")
receipt_abbr = frappe.db.get_value("Company", receipt_company, "abbr")
if not issue_abbr or not receipt_abbr:
    frappe.throw("Company abbreviations are required (check Company records).")

sender_ar_override = doc.get("sender_ar_account_name")
receiver_ap_override = doc.get("receiver_ap_account_name")

if sender_ar_override:
    ic_ar_name = sender_ar_override
else:
    ic_ar_name = frappe.db.get_value("Account", {
        "company": issue_company,
        "name": SENDER_AR_BASE + " - " + issue_abbr
    }, "name")

if receiver_ap_override:
    ic_ap_name = receiver_ap_override
else:
    ic_ap_name = frappe.db.get_value("Account", {
        "company": receipt_company,
        "name": RECEIVER_AP_BASE + " - " + receipt_abbr
    }, "name")

if not ic_ar_name:
    frappe.throw(
        f"Missing Intercompany Receivable in {issue_company}. "
        f"Create: '{SENDER_AR_BASE} - {issue_abbr}' under Current Assets (Is Group: No, Currency: LYD)."
    )
if not ic_ap_name:
    frappe.throw(
        f"Missing Intercompany Payable in {receipt_company}. "
        f"Create: '{RECEIVER_AP_BASE} - {receipt_abbr}' under Current Liabilities (Is Group: No, Currency: LYD)."
    )

# -----------------------------
# 1) Create Material Issue (sender)
# -----------------------------
issue_name = None
issue_source_wh = doc.get("issue_source_warehouse")
if not issue_source_wh:
    frappe.throw("Issue Source Warehouse must be set.")
wh_company_issue = frappe.db.get_value("Warehouse", issue_source_wh, "company")
if wh_company_issue != issue_company:
    frappe.throw(f"Source Warehouse {issue_source_wh} belongs to {wh_company_issue}, not {issue_company}.")

se_issue = frappe.get_doc({
    "doctype": "Stock Entry",
    "stock_entry_type": "Material Issue",
    "company": issue_company,
    "posting_date": posting_date,
    "posting_time": posting_time,
    "set_posting_time": set_posting_time,
    "remarks": (doc.get("remarks") or "") + f"\nSource: {doc.doctype} {doc.name}",
})
for d in items:
    item_code = d.get("item_code")
    uom = d.get("uom") or frappe.db.get_value("Item", item_code, "stock_uom")
    conversion_factor = d.get("conversion_factor") or 1
    description = d.get("description") or (frappe.db.get_value("Item", item_code, "description") or item_code)

    row = {
        "item_code": item_code,
        "s_warehouse": issue_source_wh,
        "qty": d.get("qty"),
        "uom": uom,
        "conversion_factor": conversion_factor,
        "description": description,
    }
    serial_no = d.get("serial_no")
    batch_no = d.get("batch_no")
    if serial_no: row["serial_no"] = serial_no
    if batch_no: row["batch_no"] = batch_no
    se_issue.append("items", row)

se_issue.flags.ignore_permissions = True
se_issue.insert()
se_issue.submit()
issue_name = se_issue.name

# -----------------------------
# 2) Create Material Receipt (receiver)
# -----------------------------
receipt_name = None
receipt_target_wh = doc.get("receipt_target_warehouse")
if not receipt_target_wh:
    try:
        if exists_se(issue_name):
            se_issue = frappe.get_doc("Stock Entry", issue_name)
            if se_issue.docstatus == 1: se_issue.cancel()
    except Exception as cancel_err:
        frappe.log_error(title="Intercompany Transfer: rollback failed", message=f"Could not cancel Material Issue {issue_name}: {cancel_err}")
    frappe.throw("Receipt Target Warehouse must be set.")

wh_company_receipt = frappe.db.get_value("Warehouse", receipt_target_wh, "company")
if wh_company_receipt != receipt_company:
    try:
        if exists_se(issue_name):
            se_issue = frappe.get_doc("Stock Entry", issue_name)
            if se_issue.docstatus == 1: se_issue.cancel()
    except Exception as cancel_err:
        frappe.log_error(title="Intercompany Transfer: rollback failed", message=f"Could not cancel Material Issue {issue_name}: {cancel_err}")
    frappe.throw(f"Target Warehouse {receipt_target_wh} belongs to {wh_company_receipt}, not {receipt_company}.")

se_receipt = frappe.get_doc({
    "doctype": "Stock Entry",
    "stock_entry_type": "Material Receipt",
    "company": receipt_company,
    "posting_date": posting_date,
    "posting_time": posting_time,
    "set_posting_time": set_posting_time,
    "remarks": (doc.get("remarks") or "") + f"\nSource: {doc.doctype} {doc.name}",
})
price_list = doc.get("price_list")

for d in items:
    item_code = d.get("item_code")
    uom = d.get("uom") or frappe.db.get_value("Item", item_code, "stock_uom")
    conversion_factor = d.get("conversion_factor") or 1
    description = d.get("description") or (frappe.db.get_value("Item", item_code, "description") or item_code)

    incoming_rate = 0.0
    rate_val = d.get("rate")
    if rate_val is not None:
        try:
            incoming_rate = float(rate_val)
        except Exception:
            incoming_rate = 0.0
    elif price_list:
        price = frappe.db.get_value("Item Price", {"item_code": item_code, "price_list": price_list}, "price")
        if price is not None:
            try:
                incoming_rate = float(price)
            except Exception:
                incoming_rate = 0.0

    row = {
        "item_code": item_code,
        "t_warehouse": receipt_target_wh,
        "qty": d.get("qty"),
        "uom": uom,
        "conversion_factor": conversion_factor,
        "basic_rate": incoming_rate,
        "description": description,
    }
    serial_no = d.get("serial_no")
    batch_no = d.get("batch_no")
    if serial_no: row["serial_no"] = serial_no
    if batch_no: row["batch_no"] = batch_no
    se_receipt.append("items", row)

se_receipt.flags.ignore_permissions = True
se_receipt.insert()
se_receipt.submit()
receipt_name = se_receipt.name

# -----------------------------
# 3) Valuation for JEs (GL first, then SLE)
# -----------------------------
if not exists_se(issue_name):
    frappe.throw(f"Source Stock Entry not found or not submitted: {issue_name}.")
if not exists_se(receipt_name):
    try:
        if exists_se(issue_name):
            se_i = frappe.get_doc("Stock Entry", issue_name)
            if se_i.docstatus == 1: se_i.cancel()
    except Exception as cancel_err:
        frappe.log_error(title="Intercompany: missing Receipt SE; rollback Issue failed", message=str(cancel_err))
    frappe.throw(f"Target Stock Entry not found or not submitted: {receipt_name}.")

# Issue (sender)
gl_issue = fetch_gl(issue_name, issue_company)
sender_expense_account = None
sender_amount = 0.0
if gl_issue:
    for e in gl_issue:
        acc = e.get("account")
        try:
            debit_val = float(e.get("debit") or 0)
        except Exception:
            debit_val = 0.0
        if debit_val > 0:
            sender_expense_account = acc
            sender_amount = sender_amount + debit_val
if sender_amount <= 0:
    sender_amount = sum_abs_sle_value(issue_name, issue_company)
    if not sender_expense_account:
        sender_expense_account = (
            frappe.db.get_value("Account", {"company": issue_company, "account_name": "Stock Adjustment"}, "name")
            or frappe.db.get_value("Account", {"company": issue_company, "name": f"Stock Adjustment - {issue_abbr}"}, "name")
        )
    if not sender_expense_account:
        frappe.throw(f"Could not resolve a Stock Adjustment account for {issue_company}.")

# Receipt (receiver)
gl_receipt = fetch_gl(receipt_name, receipt_company)
receiver_expense_account = None
receiver_amount = 0.0
if gl_receipt:
    for e in gl_receipt:
        acc = e.get("account")
        try:
            credit_val = float(e.get("credit") or 0)
        except Exception:
            credit_val = 0.0
        if credit_val > 0:
            receiver_expense_account = acc
            receiver_amount = receiver_amount + credit_val
if receiver_amount <= 0:
    receiver_amount = sum_abs_sle_value(receipt_name, receipt_company)
    if not receiver_expense_account:
        receiver_expense_account = (
            frappe.db.get_value("Account", {"company": receipt_company, "account_name": "Stock Adjustment"}, "name")
            or frappe.db.get_value("Account", {"company": receipt_company, "name": f"Stock Adjustment - {receipt_abbr}"}, "name")
        )
    if not receiver_expense_account:
        frappe.throw(f"Could not resolve a Stock Adjustment account for {receipt_company}.")

# Optional symmetry: if one side > 0 and the other == 0, copy the non-zero
if SYNC_AMOUNT_FROM_COUNTERPART:
    if sender_amount <= 0 and receiver_amount > 0:
        sender_amount = receiver_amount
    elif receiver_amount <= 0 and sender_amount > 0:
        receiver_amount = sender_amount

# -----------------------------
# 3.a) Decide whether to skip JEs (zero valuation path) or proceed
# -----------------------------
SKIP_JE = 0
if sender_amount == 0.0 and receiver_amount == 0.0:
    if ALLOW_ZERO_VALUATION:
        # Inform user and link SEs; skip JEs
        try:
            frappe.db.set_value(doc.doctype, doc.name, "material_issue", issue_name)
            frappe.db.set_value(doc.doctype, doc.name, "material_receipt", receipt_name)
        except Exception as link_err:
            frappe.log_error(title="Interco: link SE (zero valuation) failed", message=str(link_err))
        frappe.msgprint(
            msg=f"Intercompany transfer {doc.name} completed.\n"
                f"Journal Entries were skipped because both Issue and Receipt resolved to zero valuation.\n"
                f"To post JEs, set a non-zero rate on the line or configure an Item Price/valuation.",
            title="Intercompany Transfer (Zero Valuation)",
            indicator="blue"
        )
        SKIP_JE = 1
    else:
        # Block: cancel SEs and throw
        try:
            if exists_se(issue_name):
                se_i = frappe.get_doc("Stock Entry", issue_name)
                if se_i.docstatus == 1: se_i.cancel()
            if exists_se(receipt_name):
                se_r = frappe.get_doc("Stock Entry", receipt_name)
                if se_r.docstatus == 1: se_r.cancel()
        except Exception as cancel_err:
            frappe.log_error(title="Intercompany JE: rollback failed (zero valuation)", message=str(cancel_err))
        frappe.throw(f"Both Issue {issue_name} and Receipt {receipt_name} resolved to zero valuation; JEs not created.")

# -----------------------------
# 4) Create JEs (only if not SKIP_JE)
# -----------------------------
if not SKIP_JE:
    sender_je_name = None
    receiver_je_name = None
    try:
        # Sender JE: Dr Intercompany Receivable, Cr Stock Adjustment
        je_sender = frappe.get_doc({
            "doctype": "Journal Entry",
            "company": issue_company,
            "posting_date": posting_date,
            "user_remark": f"Intercompany transfer {doc.doctype} {doc.name} | Issue SE: {issue_name}",
            "set_posting_time": set_posting_time,
            "accounts": [
                {"account": ic_ar_name, "debit_in_account_currency": sender_amount, "credit_in_account_currency": 0},
                {"account": sender_expense_account, "debit_in_account_currency": 0, "credit_in_account_currency": sender_amount}
            ]
        })
        je_sender.flags.ignore_permissions = True
        je_sender.insert()
        je_sender.submit()
        sender_je_name = je_sender.name

        # Receiver JE: Dr Stock Adjustment, Cr Intercompany Payable
        je_receiver = frappe.get_doc({
            "doctype": "Journal Entry",
            "company": receipt_company,
            "posting_date": posting_date,
            "user_remark": f"Intercompany transfer {doc.doctype} {doc.name} | Receipt SE: {receipt_name}",
            "set_posting_time": set_posting_time,
            "accounts": [
                {"account": receiver_expense_account, "debit_in_account_currency": receiver_amount, "credit_in_account_currency": 0},
                {"account": ic_ap_name, "debit_in_account_currency": 0, "credit_in_account_currency": receiver_amount}
            ]
        })
        je_receiver.flags.ignore_permissions = True
        je_receiver.insert()
        je_receiver.submit()
        receiver_je_name = je_receiver.name

    except Exception as je_err:
        # Cancel JEs if any were created
        try:
            if sender_je_name:
                je_s = frappe.get_doc("Journal Entry", sender_je_name)
                if je_s.docstatus == 1: je_s.cancel()
        except Exception:
            pass
        try:
            if receiver_je_name:
                je_r = frappe.get_doc("Journal Entry", receiver_je_name)
                if je_r.docstatus == 1: je_r.cancel()
        except Exception:
            pass

        # Cancel SEs to keep atomicity (block submission)
        try:
            if exists_se(issue_name):
                se_i = frappe.get_doc("Stock Entry", issue_name)
                if se_i.docstatus == 1: se_i.cancel()
            if exists_se(receipt_name):
                se_r = frappe.get_doc("Stock Entry", receipt_name)
                if se_r.docstatus == 1: se_r.cancel()
        except Exception as cancel_err:
            frappe.log_error(title="Intercompany JE: rollback failed", message=f"Could not cancel SEs after JE failure: {cancel_err}")
        raise je_err

    # -----------------------------
    # 5) Link SEs & JEs back to source (after success)
    # -----------------------------
    try:
        frappe.db.set_value(doc.doctype, doc.name, "material_issue", issue_name)
        frappe.db.set_value(doc.doctype, doc.name, "material_receipt", receipt_name)
    except Exception as link_err:
        frappe.log_error(title="Interco: link SE failed", message=str(link_err))

    try: frappe.db.set_value(doc.doctype, doc.name, "sender_journal_entry", sender_je_name)
    except Exception: pass
    try: frappe.db.set_value(doc.doctype, doc.name, "receiver_journal_entry", receiver_je_name)
    except Exception: pass
else:
    # Already linked SEs in zero-valuation branch and informed user via msgprint
    pass
